package P_389_找不同;

public class Solution {

    public char findTheDifference(String s, String t) {

        int[][] has = new int[26][1];

        // init
        for(int i = 'a'; i <='z';i++){
            has[i-'a'][0] = 0;
        }

        for (char c : s.toCharArray()){
            has[c-'a'][0]++;
        }

        for (char c : t.toCharArray()){
            has[c-'a'][0]--;
            if (has[c-'a'][0] < 0){
                return c;
            }

        }
        return 'a';
    }

    public static void main(String[] args) {
//        System.out.println(new Solution().findTheDifference("abdc", "adcbe"));
        System.out.println(new Solution().findTheDifference("",
                ""));
    }
}
